mapper.select:
select ai.id, ai.name, ai.introduction, ai.district_id, ai.district_name, ai.city_id, ai.city_name, ai.province_id, ai.province_name, ai.address_detail, ai.latitude, ai.longitude, ai.phone, ai.is_release, ifnull(ri.room_count, 0) total_room_count, ifnull(ri.room_count, 0) - ifnull(la.lease_count, 0) free_room_count from (select id, name, introduction, district_id, district_name, city_id, city_name, province_id, province_name, address_detail, latitude, longitude, phone, is_release from apartment_info
is_deleted = 0
and province_id = #{queryVo.provinceId}
and province_id = #{queryVo.provinceId}
and district_id = #{queryVo.districtId}
) ai
left join (select apartment_id, count(*) room_count from room_info where is_deleted = 0 and is_release = 1 group by apartment_id) ri on ai.id = ri.apartment_id
left join (select apartment_id, count(*) lease_count from lease_agreement where is_deleted = 0 and status in (2, 5, 7) group by apartment_id) la on ai.id = la.apartment_id
